import React, { useEffect, useMemo, useState } from 'react'
import { useTable } from 'react-table';
import { fetchTodos } from '../redux/todosAction';
import { COLUMNS } from './column';
import { connect } from 'react-redux';
import './DisplayTable.css';
// import axios from 'axios';

function DisplayTable({ todosData, fetchTodos }) {
    // const [todos, setTodos] = useState([]);

    useEffect(() => {
        fetchTodos()
    }, [])
    console.log(todosData)
    // const fetchTodos = async () => {
    //     const url = 'https://jsonplaceholder.typicode.com/todos';
    //     try{
    //         const response = await axios.get(url);
    //         const responseData = await response.data;
    //         setTodos(responseData)
    //         console.log(responseData)
    //         console.log(todos)
    //     } catch (error) {
    //         console.log("Error------>", error)
    //     }
    // }

    // const columns = useMemo(() => COLUMNS, []);
    // const data = useMemo(() => todos, []);

    const tableInstance = useTable({ 
        columns: COLUMNS, 
        data: todosData 
    })

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
    } = tableInstance

    return (
        <table {...getTableProps()}>
            <thead>
                {
                    headerGroups.map(headerGroup => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {
                                headerGroup.headers.map(column => (
                                    <th {...column.getHeaderProps()}>
                                        {
                                            column.render('Header')
                                        }
                                    </th>
                                ))
                            }
                        </tr>
                    ))
                }
            </thead>
            <tbody {...getTableBodyProps()}>
                {
                    rows.map(row => {
                        prepareRow(row)
                        return (
                            <tr {...row.getRowProps()}>
                                {
                                    row.cells.map(cell => (
                                        <td {...cell.getCellProps()}>
                                            {
                                                cell.render('Cell')
                                            }
                                        </td>
                                    ))
                                }
                            </tr>
                        )
                    })
                }
            </tbody>
        </table>
    )
}

const mapStateToProps = state => {
    return{
        todosData: state.todosData
    }
}

const mapDispatchToProps = dispatch => {
    return{
        fetchTodos: () => dispatch(fetchTodos())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(DisplayTable)
// export default DisplayTable;
