import { TODOS_FAILURE, TODOS_REQUEST, TODOS_SUCCESS } from "./todosTypes"
import axios from 'axios';

export const todosRequest = () => {
    return {
        type: TODOS_REQUEST,
    }
}

export const todosSuccess = (todos) => {
    return {
        type: TODOS_SUCCESS,
        payload: todos
    }
}

export const todosFailure = (error) => {
    return {
        type: TODOS_FAILURE,
        payload: error
    }
}

export const fetchTodos = () => {
    return (dispatch) => {
        dispatch(todosRequest)
        const url = 'https://jsonplaceholder.typicode.com/todos';
        axios.get(url).then(response => {
            const todos = response.data 
            dispatch(todosSuccess(todos))
        }).catch(error => {
            const message = error.message;
            dispatch(todosFailure(message))
        });
    }
}
