import React, { useEffect, useMemo } from 'react'
import { useTable } from 'react-table';
import { fetchTodos } from '../redux/todosAction';
import { COLUMNS } from './column';
import { connect } from 'react-redux';
import './DisplayTable.css';

function DisplayTable({ todosData, fetchTodos }) {
    useEffect(() => {
        fetchTodos()
    }, [])

    const columns = useMemo(() => COLUMNS, []);
    const data = useMemo(() => todosData, []);

    const tableInstance = useTable({ columns, data })

    const {
        getTableProps,
        getTableBodyProps,
        headerGroups,
        rows,
        prepareRow,
    } = tableInstance

    return (
        <table {...getTableProps()}>
            <thead>
                {
                    headerGroups.map(headerGroup => (
                        <tr {...headerGroup.getHeaderGroupProps()}>
                            {
                                headerGroup.headers.map(column => (
                                    <th {...column.getHeaderProps()}>
                                        {
                                            column.render('Header')
                                        }
                                    </th>
                                ))
                            }
                        </tr>
                    ))
                }
            </thead>
            <tbody {...getTableBodyProps()}>
                {
                    rows.map(row => {
                        prepareRow(row)
                        return (
                            <tr {...row.getRowProps()}>
                                {
                                    row.cells.map(cell => (
                                        <td {...cell.getCellProps()}>
                                            {
                                                cell.render('Cell')
                                            }
                                        </td>
                                    ))
                                }
                            </tr>
                        )
                    })
                }
            </tbody>
        </table>
    )
}

const mapStateToProps = state => {
    return{
        todosData: state.todo
    }
}

const mapDispatchToProps = dispatch => {
    return{
        fetchTodos: () => dispatch(fetchTodos())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(DisplayTable)
